import React from "react";
import "./TodoList.css";

// map함수
export default function TodoList({ todos, isActive }) {
  const deleteHandler = () => {
    alert("삭제");
  };

  return (
    <section className="TodoList_container">
      <div>
        <h4 className="title">{isActive === true ? "Active 😥" : "Done 😎"}</h4>
        {todos
          .filter((item) => item.isDone === !isActive) //!isActive == false
          .map((item) => {
            return (
              <div className="card_wrap" key={item.id}>
                <h3>{item.title}</h3>
                <p>{item.contents}</p>
                <button className="btn">{isActive ? "완료" : "취소"}</button>
                <button className="btn" onClick={deleteHandler}>
                  삭제
                </button>
              </div>
            );
          })}
      </div>
    </section>
  );
}
