import logo from "./logo.svg";
import "./App.css";

function App() {
  return (
    <div>
      <header>My Todo</header>
      {/* Todo 생성 Form */}
      <section>
        <form>
          <p>
            {" "}
            제목
            <input type="text" />
          </p>
          <p>
            {" "}
            내용
            <input type="text" />
          </p>
          <button>Add</button>
        </form>
      </section>
      {/* Todo active, done section */}
      <section>
        <h4>Active</h4>
        <h4>Done</h4>
      </section>
      <footer>Copyright : 휘인</footer>
    </div>
  );
}

export default App;
