import React, { useState } from "react";
import { v4 as uuid } from "uuid";

export default function Input({ setTodos }) {
  const [title, setTitle] = useState("");
  const [contents, setContents] = useState("");

  const submitHandler = (event) => {
    event.preventDefault();

    const newTodos = {
      title: title,
      contents: contents,
      isDone: false,
      id: uuid(),
    };
    // 지금 세팅된 title과 contents를 todos에 넣어주는 작업.
    setTodos((prev) => {
      return [...prev, newTodos];
    });

    // Todo 생성 후, input 초기화
    setTitle("");
    setContents("");
  };

  const titleInputHandler = (event) => {
    setTitle(event.target.value);
  };
  const contentsInputHandler = (event) => {
    setContents(event.target.value);
  };

  return (
    <section>
      <form onSubmit={submitHandler}>
        <p>
          제목 :
          <input type="text" onChange={titleInputHandler} value={title} />
        </p>
        <p>
          내용 :
          <input type="text" onChange={contentsInputHandler} value={contents} />
        </p>
        <button>Add</button>
      </form>
    </section>
  );
}
