import "./App.css";
import React, { useState } from "react";
import Header from "./components/Header";
import Input from "./components/Input";
import { v4 as uuid } from "uuid";

function App() {
  // console.log(uuid()); 겁나 잘 나옴

  const [todos, setTodos] = useState(initialState);
  const [title, setTitle] = useState("");

  return (
    <div>
      <Header>My TodoList</Header>

      {/* Todo 생성 Form */}
      <Input />

      {/* Todo active, done section */}
      <section>
        <h4>Active</h4>
        <h4>Done</h4>
      </section>

      <footer>Copyright : 휘인</footer>
    </div>
  );
}

export default App;

// 기본 설정값
const initialState = [
  {
    title: "제목인디",
    contents: "내용인디",
    isDone: false,
    id: uuid(),
  },
  {
    title: "빨래",
    contents: "수건 빨래",
    isDone: true,
    id: uuid(),
  },
  {
    title: "투미새되기",
    contents: "TodoList에 절여져버림",
    isDone: false,
    id: uuid(),
  },
];
